import React, {useEffect, useReducer, useState} from 'react'
import { Routes, Route } from 'react-router-dom'
import axios from 'axios'

import Header from './components/Header'
import Products from './pages/Products'
import Cart from './pages/Cart'
import Product from './pages/Product'
import cartReducer from "./reducers/cartReducer";
import {CartContext,CartReducerDispatcher} from "./contexts/cartContext";

// **სავალდებულო
// გადააკეთეთ კლას კომპონენტები თანამედროვე ფუნქციურ კომპონენტებად.

// **დამატებითი
// შეძლებისდაგვარად გასტილეთ უკეთესად
// დაუმატეთ cartიდან წაშლის ფუნქციონალი
// დაუმატეთ ფუნქციონალი რომ ერთიდაიგივე აითემი, მრავალჯერ არ განმეორდეს,
// უბრალოდ იყოს რაოდენობა. მაგ: თუ ვიყიდი 3 ქურთუკს, უნდა მიჩანდეს რომ ვიყიდე 3 ცალი
// და არ გამომიჩნდეს 3ჯერ
// ასევე კარგი იქნება დაამატოთ, რაოდენობი გაზრდა-დაპატარავების ფუნქციონალი

const App = ()=> {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(false);
    const [cart, dispatch] = useReducer(cartReducer,[]);

    const getData = async () => {
        const response = await axios.get('https://fakestoreapi.com/products')
        setProducts(response.data);
        setLoading(false);
    }

    const handleAdd = product => {
        dispatch({
            type:'add_item',
            payload:product
        });
    }

    useEffect(() => {
        setLoading(true);
        getData().catch((err) => {
            console.log(err)
            setLoading(false);
        })
    }, []);

    return (
        <CartReducerDispatcher.Provider value={dispatch}>
            <CartContext.Provider value={cart}>
                <div className="flex flex-col">
                    <Header itemCount={cart.length}/>
                    <Routes>
                        <Route path='/' exact
                               element={<Products products={products}
                                                  handleAddProduct={handleAdd}
                                                  loading={loading}/>}/>
                        <Route path='/products/:productId' exact element={<Product handleAddProduct={handleAdd}/>}/>
                        <Route path='/cart' exact element={<Cart products={cart}/>}/>
                    </Routes>
                </div>
            </CartContext.Provider>
        </CartReducerDispatcher.Provider>
    )
}

export default App
